mapper.insert:
INSERT INTO BOARD
	( ID
	  , NAME
	  , DESCRIPTION

	  , HITCOUNTER					  
	  )
VALUES ( #{id}
	  , #{name}
	  , #{description}

	  , #{hitCounter}				 
	  )

mapper.update:
UPDATE BOARD
SET ID=#{id}
	, NAME=#{name}
	, DESCRIPTION=#{description}				
	  WHERE ID=#{id}


UPDATE BOARD
SET hitCounter = hitCounter + 1
WHERE ID=#{id}
